import "./App.css";
import React from 'react';

import jsQR from "jsqr";

class App extends React.Component {

  constructor(props) {
    super(props)

    var video = document.createElement("video");
    this.canvasElement = React.createRef()
    var canvas = this.canvasElement.getContext("2d");
    // TODO move from ref to state
    var loadingMessage = document.getElementById("loadingMessage");
    var outputContainer = document.getElementById("output");
    var outputMessage = document.getElementById("outputMessage");
    var outputData = document.getElementById("outputData");
  }


  render() {
    return (
      <div className="App">
        <h1>jsQR Demo</h1>
        <a id="githubLink" href="https://github.com/cozmo/jsQR">
          View documentation on Github
        </a>
        <p>Pure JavaScript QR code decoding library.</p>
        <div id="loadingMessage">
          ðŸŽ¥ Unable to access video stream (please make sure you have a webcam
          enabled)
        </div>
        <canvas ref={this.canvasElement} id="canvas" hidden></canvas>
        <div id="output" hidden>
          <div id="outputMessage">No QR code detected.</div>
          <div hidden>
            <b>Data:</b> <span id="outputData"></span>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
