import "./App.css";
import React from 'react';

import jsQR from "jsqr";

class App extends React.Component {

  constructor(props) {
    super(props)

    this.canvasElement = React.createRef()
    // TODO move from ref to state
    this.loadingMessage = React.createRef()
    this.output = React.createRef()
    this.outputData = React.createRef()
  }

  tick() {
    this.loadingMessage.innerText = "âŒ› Loading video..."
    requestAnimationFrame(this.tick);
  }


  componentDidMount() {
    var canvas = this.canvasElement.current.getContext("2d");
    var video = document.createElement("video");
    // Use facingMode: environment to attemt to get the front camera on phones
    const that = this;
    navigator.mediaDevices.getUserMedia({ video: { facingMode: "environment" } }).then(function(stream) {
      video.srcObject = stream;
      video.setAttribute("playsinline", true); // required to tell iOS safari we don't want fullscreen
      video.play();
      requestAnimationFrame(that.tick);
    });
  }


  drawLine(begin, end, color) {
    const context = this.canvasElement.current.getContext("2d");
    context.beginPath();
    context.moveTo(begin.x, begin.y);
    context.lineTo(end.x, end.y);
    context.lineWidth = 4;
    context.strokeStyle = color;
    context.stroke();
  }


  render() {
    return (
      <div className="App">
        <h1>jsQR Demo</h1>
        <a id="githubLink" href="https://github.com/cozmo/jsQR">
          View documentation on Github
        </a>
        <p>Pure JavaScript QR code decoding library.</p>
        <div id="loadingMessage" ref={this.loadingMessage}>
          ðŸŽ¥ Unable to access video stream (please make sure you have a webcam
          enabled)
        </div>
        <canvas ref={this.canvasElement} id="canvas" hidden></canvas>
        <div id="output" hidden ref={this.output}>
          <div id="outputMessage" ref={this.outputMessage}>No QR code detected.</div>
          <div hidden>
            <b>Data:</b> <span id="outputData" ref={this.outputData}></span>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
